INSERT INTO `` (`ID`,`Forum`,`Reply`,`Navn`,`Titel`,`Tekst`,`Tid`,`Dato`,`Email`,`IP`) VALUES (118,1,0,'Kim Volksgenosse Nielsen','Sneugler (udtales \"Schnøjgler\")','Sneugleren (Nyctea scandiaca) lever på den nordlige halvkugles tundraområder..\r\nSneuglerhannen er næsten helt hvid. Hunnen derimod har brunsorte pletter i den hvide fjerdragt.\r\nSneuglerens revirsang er et kort, dumpt, lidt stønnende \"hooh\", som bliver gentaget med nogle sekunders mellemrum. Hannens advarselslyd er et bjæffende \"kræk-kræk-kræk\", hunnens er grovere og højere i tonen. Et gennemtrængende og fløjtende skrig \"siiuue\" bruges af både hunnen og de tiggende unger.\r\nSneugleren er ikke en truet dyreart, men den er sjælden.','10:05:00','2008-09-21','f1@voldgaarden.dk','91.100.16.253');
